/**
 * Module Dependencies
 */

var clst = require('../lib/clst'),
http = require('http');

/**
 * new Clst
 * @param debug {Bool} is debug modle
 * @param cpus {Number} how many forks you want create,default is the cpus length
 */
var app = new clst({
    debug : true,
    cpus : 4
});

/**
 * @method init
 * the main functions
 */
app.init = function() {
    //this.log('[worker] ' + "start worker ..." + cluster.worker.id + ',PID:' + process.pid);
    http.createServer(function(req, res) {
        res.end('8888')
    }).listen(8888);
};

/**
 * @method start
 * start the project
 */
app.start();
